INSERT INTO `webboard` (`id`, `posted_by`, `title`, `detail`, `views`, `date_create`, `date_modify`) VALUES
(2, 1, '4 ปลามงคลเลี้ยงเสริมบารมี', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.infinitydesign.in.th/wp-content/uploads/2016/11/Facebook33.jpg\" style=\"height:293px; width:560px\" /></p>\n\n<p><span style=\"font-size:16px\">&nbsp; &nbsp;<span style=\"color:#A52A2A\">แต่ก่อนอื่นก็ต้องขอบอกก่อนนะคะว่านี้เป็นความเชื่อที่มีมาตั้งแต่โบร่ำโบราณ โดยเฉพาะคนจีนที่เชื่อว่าเหล่าสัตว์มงคล เช่น มังกร ม้า สิงโต ปลา นก จะช่วยให้ธุรกิจเจริญรุ่งเรืองที่สำคัญก็เป็นจริงซะด้วยแต่ก็ต้องมาจากความขยันของเจ้าของเองด้วยนะคะถึงจะสำเร็จ<br />\n&nbsp; &nbsp;วันนี้เราเลยจะขอหยิบยกเจ้าปลามงคลนิแหละคะมาพาเพื่อนๆเลี้ยงว่าแต่จะมีชนิดไหนกันบ้างก็ตามเกาะขอบบ่อไปดูกันเลย&nbsp;</span></span></p>\n\n<p style=\"text-align:center\"><img alt=\"ปลาคาร์ฟ\" src=\"http://www.infinitydesign.in.th/wp-content/uploads/2016/11/18-450x235.jpg\" style=\"height:235px; width:450px\" title=\"ปลาคาร์ฟ\" /></p>\n\n<h1 style=\"text-align:center\"><span style=\"color:#FF0000\"><strong>1. ปลาคาร์ฟ&nbsp;</strong></span></h1>\n\n<h1><br />\n&nbsp;<span style=\"color:#8B4513\"><span style=\"font-size:16px\">&nbsp; ขอเริ่มจากปลายอดฮิตติดชาร์ทนั้นก็คือ ปลาคาร์ฟ ซึ่งชื่อปลาคาร์ฟตามหลักคนจีนมาจากคำว่า&nbsp;หลี่&nbsp;หมายถึง ผลกำไรหรือประโยชน์ ปลาคาร์ฟ จึงเป็นสัญลักษณ์ของความปรารถนาที่จะได้มาซึ่งผลกำไรจากการทำธุรกิจนั้นเองค่ะ ส่วนจำนวนการเลี้ยงก็จะเริ่มตั้งแต่&nbsp;1 ตัว ,3 ,5,7 หรือ 9 ตัว ถึงจะถือว่าเป็นมงคลคะ<br />\n&nbsp;&nbsp;&nbsp;ส่วนถิ่นฐานเดิมของปลาคาร์ฟจะมาจากประเทศอิหร่าน ก่อนจะเข้ามาอิทธิพลกับประเทศญุี่ปุ่น ซึ่งตามความเชื่อของฮวงจุ้ยญี่ปุ่นก็บอกไว้ว่าสายพันธุ์ที่ดีและเป็นมงคลก็คือ&nbsp;<strong>&ldquo;พันธุ์โคฮากุ และ ตันโจ&rdquo;&nbsp;</strong>สีประจำตัวคือสีแดงและสีขาวเป็น (สีแดง&nbsp;&nbsp;หมายถึง&nbsp;:&nbsp;<strong>อำนาจและโชคลาภ ส่วน /</strong>สีขาว&nbsp;&nbsp;หมายถึง :&nbsp;<strong>สิ่งที่ดีมีมงคล)&nbsp;</strong>รู้แล้วก็ลองไปเลือกซื้อให้ถูกตามหลักนะคะเผื่อชีวิตจะดีดี๊กับเค้าบ้าง</span></span></h1>\n\n<hr />\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><img alt=\"ปลาเงิน ปลาทอง\" src=\"http://www.infinitydesign.in.th/wp-content/uploads/2016/11/34c45233906491b4bffb42f7ee854fb8-450x386.jpg\" style=\"height:386px; width:450px\" title=\"ปลาเงิน ปลาทอง\" /></p>\n\n<h1 style=\"text-align:center\"><span style=\"color:#FF0000\"><strong>2.ปลาเงิน ปลาทอง</strong></span></h1>\n\n<h1><br />\n<span style=\"color:#A52A2A\">&nbsp; &nbsp;</span><span style=\"font-size:16px\"><span style=\"color:#A52A2A\">ปลาเงิน ปลาทอง มีต้นกำเนิดจากประเทศจีนและญี่ปุ่น เป็นสัญลักษณ์ของการมีเงินทองล้นหลาม คนจีนจึงนิยมให้กันเป็นของมงคล ส่วนจำนวนการเลี้ยงตามความเชื่อบอกไว้ว่าให้เลือก&nbsp;8 ตัว เป็นสีทอง และสีดำอีก 1 ตัว</span><br />\n<span style=\"color:#A52A2A\">&nbsp;&nbsp;&nbsp;เพราะในความหมายบอกไว้ว่า &nbsp;8 เป็นเลขที่แสดงถึงความไม่สิ้นสุดหมายถึงทำให้เรามีเงินทองไม่มีที่สิ้นสุด ส่วนปลาสีดำ 1 ตัว คือหลักความเชื่อของไทยที่นับถือว่าเลข 9 เป็นเลขมงคล เลยต้องมีไว้ทั้ง 2 ศาสตร์จึงจะได้ดียิ่งๆขึ้นไปส่วนสาบพันธุ์ที่นิยมก็ได้แก่&nbsp;กลุ่มสายพันธุ์หัวสิงห์ ออรันดา หรือฮอลันดา เกล็ดแก้ว ตาโปน ริวกิ้น ตาลูกโป่ง ชูบุงกิ้น และ โคเมท เป็นต้น</span></span></h1>\n\n<hr />\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><img alt=\"ปลาหมอสี\" src=\"http://www.infinitydesign.in.th/wp-content/uploads/2016/11/113-450x300.jpg\" style=\"height:300px; width:450px\" title=\"ปลาหมอสี\" /></p>\n\n<h1 style=\"text-align:center\"><span style=\"color:#FF0000\"><strong>3.ปลาหมอสี</strong></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#A52A2A\">&nbsp; &nbsp;</span><span style=\"font-size:16px\"><span style=\"color:#A52A2A\">เป็นอีกปลาคู่บารมีของผู้เลี้ยง โดยเฉพาะสายพันธุ์<strong>&nbsp;ครอสบรีด &ldquo;Cross Breed&rdquo;</strong>&nbsp;ที่ถูกนิยมนำมาเลี้ยงเป็นอย่างมากเพราะลักษณะดี ตามการพัฒนาสายพันธุ์กระทั่งมีสรีระ ลวดลายและสีสันที่งดงาม หัวจะโหนกกลมใหญ่อันเป็นลักษณะเด่น ยิ่งใหญ่ยิ่งดี หมายถึงการมีอายุยืนยาว ทรัพย์สินมากล้น ส่วนสีของตัวปลาเป็นสีแดง หมายถึงความเป็นสิริมงคล เม็ดมุกบนตัว โดยเฉพาะบริเวณหัวหมายถึงความสว่างสดใสรุ่งเรืองนั้นเอง<br />\n&nbsp;&nbsp;&nbsp;นอกจากนี้ยังเชื่อกันว่าปลาหมอสีมีความเกี่ยวพันกับหลักโหราศาสตร์ ธาตุดิน น้ำ ลม&nbsp;ไฟ หากโชคดีก็จะเจอตัวที่มีครบทั้ง 4 ธาตุ ถือว่าเป็นสุดยอดแห่งปลาหมอสีตัวที่มีสัญลักษณ์ของธาตุไฟ หมายถึงความฉลาด กระตือรือร้น,ธาตุดิน คือความสุขุมไตร่ตรองถ้วนถี่,ธาตุลม คือสติปัญญาความคิดสร้างสรรค์จินตนาการ และธาตุน้ำ หมายถึงการทำงานที่ว่องไวรวดเร็ว</span></span></p>\n\n<hr />\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><img alt=\"ปลามังกรหรือปลาอะโรวาน่า\" src=\"http://www.infinitydesign.in.th/wp-content/uploads/2016/11/4c95c3a9a11348946a97dfbb3898a29f-450x339.jpg\" style=\"height:339px; width:450px\" title=\"ปลามังกรหรือปลาอะโรวาน่า\" /></p>\n\n<h1 style=\"text-align:center\"><span style=\"color:#FF0000\"><strong>4.ปลามังกรหรือปลาอะโรวาน่า</strong></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><span style=\"color:#A52A2A\">&nbsp; &nbsp;ปลามังกรทอง หรือ&nbsp;ปลาอโรวาน่า (Arowana) มีลักษณะเด่นที่รูปร่างคล้ายมังกร และเนื่องจากมังกรเป็นสัญลักษณ์แห่งความรุ่งเรือง มั่งคั่ง โชคลาภ อำนาจ บารมี ปลามังกรทองจึงเป็นที่รู้จักกันดีว่าเป็นปลาที่มีท่วงท่าและลีลาในการว่ายน้ำที่สง่างาม องอาจ เกล็ดมีขนาดใหญ่และสีสันแวววาว<br />\n&nbsp;&nbsp;&nbsp;อีกทั้งมีความเชื่อกันว่าเป็นปลานำโชค เพราะมีดีด้วยกัน 5 อย่าง คือ 1. เป็นตัวแทนแห่งมังกรในความเชื่อ 2. นำสุขภาพที่ดี ความสุข และโชคดีเข้าสู่บ้าน 3. ขับสิ่งชั่วร้าย หรือโชคร้ายออกจากบ้าน 4. เพิ่มความสัมพันธ์อันดีภายในครอบครัว และ 5. นำพาธุรกิจการค้าให้เจริญรุ่งเรือง นั้นเอง</span></span></p>\n\n<p>&nbsp;</p>\n\n<hr />', 23, '20/04/2016 12:38:57', ''),
(4, 1, 'ปลากินคนเทพเจ้าอูม่า ตำนานที่กลายเป็นจริง(ที่อินเดีย ณ เเม่น้ำคงคา)', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.siamscubadiving.com/board/upload1/11815833152280.jpg\" style=\"border-style:solid; border-width:5px; height:309px; width:545px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:22px\"><strong><span style=\"color:#6633ff\">ภาพฉลามวัวที่อยู่ในน้ำจืดเพื่อมาวางไข่</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp; &nbsp;<span style=\"color:#B22222\"><span style=\"background-color:#F0FFFF\">ตำนาน เทพเจ้ากินคนหลายสิบปีที่แล้วมีเรื่องเล่ากันว่ามีปลาออกมาทำร้ายคนจนไม่มี&nbsp;คนกล้าออกมาตักน้ำในแม่น้ำคงคา แม่น้ำคงคามีลักษณะขุ่นๆ เมื่อก่อนคนอินเดียใช้วิธีเผาศพแล้วก็มีปลาเข้ามาทำ ร้ายและปลาตัวนี้ถูกเรียกว่า</span><strong><span style=\"background-color:#F0FFFF\">เทพอูม่า</span></strong></span></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><span style=\"background-color:#F0FFFF\">&nbsp; &nbsp;มีการจัดส่งเจ้าหน้าที่มาตรวจสอบปลา ตัวนี้ เจ้าหน้าที่ถึงกับตกใจจึงต้องเรียกนักวิทยาศาสตร์มาตรวจสอบปลาตัวนี้แต่ เมื่อวิทยาศาสตร์เทพอูม่าก็ไม่ยอมโพล่ตัวออกมาให้เห็น&nbsp;จึง จำเป็นต้องใช้เหยื่อล่อโดยการใช้แพะและให้คนถ่อไปที่กลางน้ำไม่นานเทพ เจ้าอูม่า ก็ปรากฎตัวขึ้นและเข้าจะเอาเหยื่อก็แพะล้มและเทพเจ้าอูม่าได้ทำร้ายเจ้า หน้าที่บาดแผลมากมายจนจึงก็ความตายเมื่อ เจ้าที่เห็นเทพเจ้าอูม่าก็ตกลง เทพเจ้าอูม่ามีขนาดตัวยาว&nbsp;4&nbsp;เมตร มีคลีบเป็นกระโดง และมีหางเป็นกระดงผิวหนังของเขาไม่มีเกล็ด </span></span></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><span style=\"background-color:#F0FFFF\">&nbsp; &nbsp;จากที่นักวิทยาศาลตร์ว่าจะจับเป็นเลยเปลี่ยนใจเป็นจับตายเจ้าหน้าที่ใข้เวลา&nbsp;</span><strong><span style=\"background-color:#F0FFFF\">หลายวันที่จะพบตัวและเมื่อพบตัวก็ยิงจหมวกเทพอูม่าก็ลากจมวกไปดำลงใต้น้ำ และปล่อยทิ้งไว้&nbsp;</span></strong><strong><span style=\"background-color:#F0FFFF\">2&nbsp;วัน และเมื่อผ่านไป&nbsp;2&nbsp;วัน&nbsp;</span></strong><span style=\"background-color:#F0FFFF\">แล้ว เจ้าหน้าที่ตัดสินใจดำลงไปใต้น้ำจนพบอุโมงใต้น้ำที่เชื่อมต่อกับแม่น้ำ อีกสายแต่เจ้าหน้าที่ไม่กล้าดำลงไปด้วยสายน้ำที่แรงมาก เลยทิ้งปริศนาเทพอูม่าอยู่เช่นนั้นและเมื่อไม่นานนักวิทยาศาสตร์ได้พบเรื่อง น่ากลัวขึ้นที่แม่น้ำอะเมลซอล ขนาดตัววัดเป็นแนวยาวได้&nbsp;4&nbsp;เมตร มันคือ&nbsp;ปลาฉลามวัวแหล่ง น้ำที่เข้าไปพบ คือ ซุ่มน้ำอเมลซอล และนักวิทยาศาสตร์ได้รู้ว่าปลาฉลามวัวนี้สามารถอาศัยอยู่ในน้ำจืดได้และวัว ฉลากวัวนี้มีลักษณะชอบไหว้ทวนน้ำ มีลักษณะนิสัยดุร้ายมาก และฉลาม วัวมีนิสัยที่ชอบกินกันเองตัวไหนแข็งแรงกว่าก็จะกินตัวที่อ่อนแออ่อน เติบโตในน้ำแม่จนกระทั้งเป็นวัยรุ่นและว่ายกลับไปที่ทะเลเพื่อไปสืบพันธุ์ ต่อไป</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ฉลามหัวบาตร\" src=\"http://www.bloggang.com/data/vinitsiri/picture/1306079111.jpg\" style=\"height:200px; width:350px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"ผลการค้นหารูปภาพสำหรับ ฉลามหัวบาตร\" src=\"data:image/jpeg;base64,\" style=\"height:200px; width:350px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#FF0000\"><span style=\"font-size:16px\">ฉลามหัวบาตรหรือฉลามวัว สามารถอาศัยอยู่ได้ทั้งน้ำเค็มเเละน้ำจืด โดยมีนิสัยดุร้าย</span></span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://www.thairath.co.th/media/NjpUs24nCQKx5e1D7IKv4BcWQacfPuFNjFYjkRcQiCK.jpg\" style=\"border-style:solid; border-width:5px; height:337px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#B22222\"><span style=\"font-size:14px\">ชาวประมงที่ราชบุรี วางข่ายในแม่น้ำแม่กลองติด &lsquo;ลูกฉลามหัวบาตร&rsquo; น้ำหนัก 5 กิโลกรัม<br />\r\nที่มา:&nbsp;<a href=\"http://www.thairath.co.th/content/513477\">http://www.thairath.co.th/content/513477</a></span></span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.siamscubadiving.com/board/upload1/11815827622280.gif\" style=\"border-style:solid; border-width:13px; height:340px; width:545px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000FF\"><span style=\"font-size:22px\">ภาพจำลองอวัยวะต่างๆของปลาฉลามหัวบารตหรือฉลามวัว</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:20px\">อ้างอิง:&nbsp;<a href=\"https://www.youtube.com/watch?v=pnA5DT3ilQw\">https://www.youtube.com/watch?v=pnA5DT3ilQw</a></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n', 4, '16/05/2017 05:02:46', '');
INSERT INTO `webboard` (`id`, `posted_by`, `title`, `detail`, `views`, `date_create`, `date_modify`) VALUES
(5, 1, 'ปลามังกรตามหลักมาตรฐานมาดูกันว่ามีกี่เเบบมีกี่สายพันธ์', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164392.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:22px\"><strong><span style=\"background-color:#E6E6FA\">1. Asian Arowana สายเอเซีย&nbsp;</span></strong><span style=\"background-color:#E6E6FA\">โดยแบ่งย่อยออกเป็น 4 สายพันธุ์ดังนี้</span></span><br />\r\n<br />\r\n<span style=\"color:#DAA520\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- ทองมาเลเซีย (Malayan Bonytongue)</span></strong></span></span><span style=\"font-size:14px\"><span style=\"background-color:#FFFFE0\">&nbsp;</span><span style=\"background-color:#FFF0F5\">หรือที่เรียกสั้นๆ ว่า &ldquo;</span><u><span style=\"background-color:#FFF0F5\">ทองมาเลย์</span></u><span style=\"background-color:#FFF0F5\">&rdquo; ปลามังกรสายพันธุ์นี้มีต้นกำเนิดอยู่ที่ประเทศมาเลเซียเพื่อนบ้านของเรานี่เอง แม้ชื่อจะบอกว่าเป็น &ldquo;ทอง&rdquo; แต่จริงๆ แล้ว Base สีมีหลายสีไม่ว่าจะเป็น สีทอง สีเขียว สีน้ำเงิน และ สีม่วง ซึ่งแต่ละสีความสวยงามและราคาก็แตกต่างกันไป ตัวอย่างเช่น หากพื้นเกล็ดเป็นสีทองก็จะเป็นทองแวววาวเงางาม และสุกสว่าง อร่ามตา แต่หากพื้นเกล็ดมีสีม่วงน้ำเงิน ดูแล้วจะเหมือนกับตัวปลาใส่เสื้อเกราะ มีมิติของสีมากขึ้นเพราะเนื้อในเกล็ดจะเป็นสีน้ำเงิน แต่มีการตัดขอบสีทองเป็นวงเกล็ดตลอดทั้งตัว ถามว่าแบบไหนสวยกว่า ? ตอบไม่ได้ครับเพราะของแบบนี้แล้วแต่คนชอบครับ แต่ไม่ว่าจะสีใดก็ตาม สำหรับสายพันธุ์ทองมาเลเซียแล้ว เมื่อปลาโตขนาดได้ที่โดยมีความยาวประมาณ 20&rdquo; เกล็ดจะขึ้นมาถึงด้านหลัง (เกล็ดแถวที่ 6) หรือที่เรียกว่า Cross Back&hellip; แต่ทว่าการข้ามหลัง ไม่ได้หมายความว่าจะข้ามหลังหมดจดทุกตัว บางตัวก็แค่เกือบข้าม บางตัวก็ข้ามแต่ยังไม่หมดจด&hellip; จะหาเนียนหมดจดทั้งทั้งหลังจริงๆ มีไม่มากนัก&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">สำหรับเรื่อง Base สีของตัวปลา หากเป็นปลาทองแบบ Full Gold ก็จะเป็นทองทั้งตัว ทั้งหัว แก้ม ครีบและหาง ซึ่งตลอดตัวจะเป็นโทนสีเดียวกัน (เหมือนทองคำแทงว่ายน้ำได้) ถ้าเป็น Gold Base ก็จะเป็นสีทองเหมือนกันแต่จะไม่หมดจดทั้งตัว สีเกล็ดด้านในยังมีอมดำ ที่หัวก็เช่นกัน ส่วนใบหางแม้จะมีละอองทองขึ้นมาเต็มแผ่นแต่ก็ยังเห็นเป็นสีส้มเข้มหรือเลือดหมู แต่หากเป็น Blue Base สีเกล็ดจะเป็นสีม่วงและยิ่งเมื่อเวลาต้องแสงไฟก็จะยิ่งเห็นได้อย่างชัดเจน เครื่องครีบของ Blue Base จะเป็นสีเลือดหมูเข้มจัด และสีของตาเป็นเป็นสีแดง&hellip;</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164401.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164413.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" /><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"background-color:#FFF0F5\">ทองมาเลย์ในวัยเล็กดูยากมากว่าโตขึ้นมาแล้วจะเป็นสีไหน ? แม้แต่ปลาคอกเดียวกันก็ไม่ได้หมายความว่าจะมี Base สีเดียวกัน บางตัวเป็น Gold Base บางตัวเป็น Blue Base&hellip; ยิ่งปัจจุบันมีการแบ่งชั้นของเกรด และชื่อสายพันธุ์ย่อยมากขึ้น ดังนั้นนอกจากจะดูสียากแล้ว ยังดูแวว (ว่าไปได้ไกลแค่ไหน) ยากอีกด้วย ปลามังกรสายพันธุ์นี้เป็นปลาที่มีราคาแพงและเปราะบางมาก เวลาเลี้ยงดูก็ต้องเอาใจใส่เป็นพิเศษหน่อย เพราะทั้งครีบและเกล็ดไม่ค่อยแข็งแรง แตกหักง่ายและการรักษาตัวก็ค่อนข้างใช้เวลานานทั้งในปลาเล็กและปลาใหญ่ ฉะนั้นในการเลี้ยงดูโดยเฉพาะการโยกย้ายปลา หากไม่รู้วิธีที่ถูกต้องก็ควรให้ผู้ที่มีความรู้มีความชำนาญมาช่วยดีกว่านะครับ เพราะถ้าปลาบาดเจ็บเสียหายแล้วจะไม่คุ้มกัน&nbsp;</span></span><br />\r\n<br />\r\n<span style=\"font-size:14px\"><span style=\"background-color:#FFF0F5\">ลักษณะโดยทั่วๆ ไปของสายพันธุ์ทองมาเลย์ก็คือ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">เครื่องครีบ</span></u><span style=\"background-color:#FFF0F5\">&rdquo; จะเล็กไม่ใหญ่เหมือนสายพันธุ์อื่น ลักษณะหัวและหน้าจะทู่สั้น แต่สีสันความแวววาวมาเป็นที่หนึ่ง ที่เกล็ดละเอียดเล็กๆ ใต้ครีบหลังจะมีการเปิดเป็นสีทองเต็มเกล็ดด้วยในขณะที่ทองอินโดจะไม่มีหรือมีก็แค่เพียงประปลายไม่ชัดเจน ทองมาเลย์โดยทั่วๆ ไปจะมีตำหนิหนึ่งที่ค่อนข้างเป็นกันมากนั่นก็คืออาการ &ldquo;ตาตก&rdquo; ในขณะที่สายพันธุ์อื่นเป็นกันน้อยกว่า</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164422.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164432.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><span style=\"color:#FF0000\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- มังกรแดง (Super Red Arowana)</span></strong></span></span>&nbsp; &nbsp; &nbsp;<span style=\"background-color:#FFF0F5\">ปลามังกรแดงมีต้นกำเนิดอยู่ที่ประเทศอินโดนีเซียครับโดยแบ่งออกเป็น 2 ชนิดคือ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">Blood Red</span></u><span style=\"background-color:#FFF0F5\">&rdquo; และ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">Chili Red</span></u><span style=\"background-color:#FFF0F5\">&rdquo; (ผมเรียกว่า &ldquo;แดงเดือด&rdquo; กับ &ldquo;แดงพริก&rdquo;)&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">สีสันของ Blood Red เมื่อโตเต็มที่จะเข้มมาก&nbsp;</span><u><span style=\"background-color:#FFF0F5\">วงเกล็ดหนา&nbsp;</span></u><span style=\"background-color:#FFF0F5\">(เรียกว่า Thick Frame) การเรียงตัวของสีอาจไม่เป็นระเบียบแต่ก็แดงเข้ม แก้มแดง ปากแดง ครีบเครื่องทุกส่วนจะแดงเข้มดูน่าเกรงขาม ส่วน Chili Red จะแดงแบบแดงเลือดนกคือแดงสว่าง ต้องตา ดูสง่างาม เมื่อโตเต็มที่วงเกล็ดโดยมากจะ&nbsp;</span><u><span style=\"background-color:#FFF0F5\">ขอบเกล็ดบาง</span></u><span style=\"background-color:#FFF0F5\">&nbsp;(Thin Frame) การตัดขอบก็จะเป็นระเบียบแดงเนียนไปทั้งตัว ตั้งแต่หัวจรดปลายหาง&hellip;&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">ลักษณะหัวส่วนใหญ่ของ Blood Red จะต่างกับ Chili Red ตรงที่ของ Blood จะเป็นทรง Sparta (หัวออกแหลมเป็นทรงสามเหลี่ยม) แต่ของ Chili จะเป็นทรง&nbsp;</span><strong><span style=\"background-color:#FFF0F5\">Spoon Head (หัวช้อน)</span></strong><span style=\"background-color:#FFF0F5\">&nbsp;ซึ่งเป็นที่นิยมมากในหมู่นักเลี้ยงชาวจีนเพราะเชื่อกันว่าจะเป็นตัวตักตวงเงินทองให้กับเจ้าของผู้เลี้ยง ความแตกต่างอีกอย่างคือปลา Blood Red ส่วนใหญ่หางจะเป็นทรงพัดซึ่งมีลักษณะกลมและกว้าง แต่ Chili Red&nbsp;</span><u><span style=\"background-color:#FFF0F5\">หางจะเป็นทรงเพชร&nbsp;</span></u><span style=\"background-color:#FFF0F5\">(Diamond Shape) ซึ่งจะเรียวยาว ดูสง่างามกว่า</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">โดยปกติปลา Chili Red จะมีราคาสูงกว่า Blood Red ในปลาวัยเล็กเป็นการยากมากที่จะแยกว่าตัวไหนเป็น Blood และตัวไหนเป็น Chili แม้แต่ผู้ที่เชี่ยวชาญที่อยู่ในวงการมานานก็ยังไม่สามารถบ่งชี้ได้อย่างชัดเจนมั่นใจ แม้แต่ในใบเซอร์รับรองสายพันธุ์จากฟาร์มก็ยังไม่กล้าระบุชี้ชัด ยิ่งปัจจุบันไม่มีปลาสายแท้ของทั้งสองนี้แล้ว แม้แต่ในประเทศแม่แบบอย่างอินโดนีเซียเองก็ตาม ส่วนใหญ่จะเป็นชื่อปลาสายใหม่ๆ ที่เอกลักษณ์เฉพาะฟาร์มมากกว่าครับ สำหรับปลาแดงหากไม่ศึกษาให้ดีพอ มีกำลังทรัพย์แต่ไม่มีความรู้ก็อาจถูกหลอกเอาปลาแดงเกรดรองมาขายก็เป็นได้ หรือแม้แต่ได้ปลาดีมาแต่เลี้ยงไม่เป็นทำให้ปลาสีซีดไม่สวยก็เสียปลาได้เช่นกัน</span><span style=\"background-color:#FFFFE0\">&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">ในทองมาเลย์มีตำหนิส่วนใหญ่คือ &ldquo;ตาตก&rdquo; ปลาแดงก็มีเช่นกันนั่นก็คือ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">ปากยื่น</span></u><span style=\"background-color:#FFF0F5\">&rdquo; (ปากล่างเกยปากบน) โดยจะเห็นชัดในปลาขนาดกลาง ถึงขนาดใหญ่ แต่ก็มีไม่น้อยครับที่ยื่นกันตั้งแต่เล็กๆ เลย</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164441.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164450.jpg\" style=\"height:250px; width:320px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><br />\r\n<strong><span style=\"background-color:#FAEBD7\"><span style=\"color:#FFA500\"><span style=\"font-size:16px\">- ทองอินโด (Red Tail Golden Arowana)</span></span></span>&nbsp;</strong><span style=\"background-color:#FFF0F5\">ทองอินโดหรือ RTG เป็นปลาระดับกลางซึ่งถ้าพูดถึงความสวยงามแล้วล่ะก็ สวยไม่แพ้ใครเลยทีเดียว ปลาที่โตแล้วเกล็ดจะเป็นสีทองเข้มแต่จะไม่แวววาว เงางาม สุกใสเหมือนทองมาเลย์ และจะขึ้นถึงเพียงเกล็ดแถวที่ 4 เท่านั้น แต่ก็อาจมีเปิดมาถึงแถวที่ 5 ไม่ว่าจะประปรายหรือเต็มแถว ดังนั้นข้อแตกต่างระหว่างปลาทองอินโดกับทองมาเลย์จึงอยู่ตรงที่ สีเกล็ด Base สี ความมันเงาแวววาว และ ข้ามหลังหรือไม่ข้ามหลัง&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">ในปลาเล็กทั้ง 2 สายพันธุ์นี้สำหรับมือใหม่อาจจะค่อนข้างดูออกยากและมีความเป็นไปได้สำหรับการสลับหลอกขายกัน โดยปกติสีของครีบก้น ครีบอกและชายน้ำของทองอินโดจะเป็นสีส้มอมแดง ส่วนหางจะแดงแค่ค่อนใบ ปลายหางด้านบนและครีบหลังจะออกน้ำตาลไหม้ เกล็ดแถว 5 จนถึงหลังจะมีน้ำตาลดำ เกล็ดละเอียดเล็กๆ ใต้ครับหลังจะไม่มีการเปิดสีของเกล็ด.. ทองอินโดเป็นมังกรสายพันธุ์ใหญ่ที่สุดในสายเอเซี่ยนโดยโตเต็มที่จะมีขนาดประมาณ 30&rdquo; (แต่โดยเฉลี่ยแล้วหากเลี้ยงในตู้จะโตได้ราวๆ 24&rdquo;)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164459.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164468.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164487.jpg\" style=\"height:400px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#FF8C00\"><span style=\"font-size:18px\">ในภาพ: ปลามังกรทองอินโดขนาดเต็มไว</span></span></strong></p>\r\n\r\n<hr />\r\n<p><span style=\"color:#00FF00\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FAEBD7\">- อโรวาน่าเขียว (Green Arowana)</span></strong></span></span><span style=\"font-size:14px\">&nbsp;<span style=\"background-color:#FFF0F5\">หรือ &ldquo;ปลาตะพัด&rdquo; ของไทยเรานี่เอง จริงๆ อโรวาน่าเขียวมีอยู่หลายที่ทั้งในประเทศไทย มาเลย์เซีย พม่า เวียดนาม ลาวและกัมพูชา (ประเทศเพื่อนบ้านทั้งนั้นเลยนะครับ) ปลาพันธุ์นี้โตขึ้นจะเป็นสีเขียวนวลและมีประกายทองทั้งตัว ใบหางจะเป็นสีเขียวเข้มแต่ไม่เต็มใบเพราะที่บริเวณปลายหางเป็นรอยขลิบขาวซึ่งถือเป็นเอกลักษณ์ของสายพันธุ์นี้</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">ราคาในท้องตลาดสำหรับปลาเล็กก็ไม่แพงเพราะฉะนั้นจึงเหมาะสำหรับผู้ที่กำลังเริ่มต้นทดลองเลี้ยง ปลามังกรเขียวมี 2 แบบคือ &ldquo;เขียวมีเซอร์&rdquo; กับ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">เขียวไม่มีเซอร์</span></u><span style=\"background-color:#FFF0F5\">&rdquo; ใบเซอร์ก็มาจากประเทศที่มีการเพาะพันธุ์ได้จริงๆ อย่างเช่นประเทศมาเลเซีย หรืออาจจะมาตัวแทนบริษัทเทรดดิ้งต่างๆ ที่ทำขึ้นมาเพื่อแสดงเครื่องหมายการค้าของตัวเองในขณะที่เขียวไม่มีเซอร์คนขายมักจะพูดว่าเป็นปลาป่าหรือปลาจับนั่นเอง จริงๆ แล้วถ้าดูปลาเป็นหรือพอมีประสบการณ์ก็ไม่จำเป็นที่จะต้องคำนึงถึงใบเซอร์ก็ได้เพราะคุณภาพก็ไม่ได้แตกต่างกันมากมาย แต่ราคาที่สนนกันตอนต้นเขียวมีเซอร์มีราคาสูงกว่าตัวที่ไม่มีเกือบเท่าตัวเลยล่ะครับ</span></span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">เพื่อนผู้อ่านหลายท่านอาจมีการสับสันระหว่าง&nbsp;</span><strong><span style=\"background-color:#FFF0F5\">&ldquo;มังกรเขียว&rdquo; กับ &ldquo;ทองอ่อน&rdquo;&nbsp;</span></strong><span style=\"background-color:#FFF0F5\">ว่าเหมือนกันหรือไม่ ในปลาวัยเล็กทั้ง 2 อย่างนี้ดูออกค่อนข้างยากเช่นกัน สำหรับผมๆ มองว่า &ldquo;มังกรเขียว&rdquo; ในวัยเล็กจะมีความเงางาม ขาวและสว่าง ในขณะที่ &ldquo;ทองอ่อน&rdquo; ไม่เงาแต่จะขาวหรือไม่ก็เป็นสีเหลืองอ่อนๆ ทั้งตัว แต่จริงๆ แล้วเคยมีผู้รู้กระซิบบอกผมว่าทั้ง 2 อย่างนี่จริงๆ มันก็พันธุ์เดียวกันเพียงแต่เขียวมากเขียวน้อย ถ้าออกเข้มๆ หน่อยก็มังกรเขียว ถ้าออกทองเงาๆ หน่อยก็ทองอ่อน แต่ราคาต่างกันประมาณ 1 เท่าครับ อย่างที่บอกไปนะครับมังกรเขียวสวยๆ หรือ &ldquo;เขียวแท้&rdquo; ที่เพาะกันในฟาร์มโดยปกติจะมีใบเซอร์รับรองสายพันธุ์ให้กับผู้ซื้อและจะขายขึ้นตู้ ส่วนถ้าเป็นทองอ่อนหรือเขียวไม่มีเซอร์ก็จะเป็นปลาถุงขายธรรมดา</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164505.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:320px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164520.jpg\" style=\"height:250px; width:320px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><span style=\"font-size:22px\"><strong><span style=\"background-color:#E6E6FA\">2. Australian Arowana</span></strong> <span style=\"background-color:#E6E6FA\">เป็นปลามังกรที่มีต้นกำเนินในทวีปออสเตรเลีย แบ่งได้ออกเป็น 2 สายพันธุ์คือ</span></span></p>\r\n\r\n<p><br />\r\n<strong><span style=\"font-size:16px\"><span style=\"color:#DDA0DD\"><span style=\"background-color:#FFFFE0\">- Australian Pearl Arowana</span></span></span><span style=\"font-size:14px\">&nbsp;</span></strong><span style=\"font-size:14px\"><span style=\"background-color:#FFF0F5\">ตอนที่ผมเขียนบทความนี้ครั้งแรก ตอนนั้นยังไม่เคยเห็นตัวจริงเลย แต่ตอนนี้ได้เห็นตัวจริงแล้ว แต่ไม่ได้มีโอกาสถ่ายรูปมาให้ ต้องขออภัยด้วยนะครับ สำหรับปลาตัวนี้เป็นปลาที่ค่อนข้างหายาก และมีราคาสูงกว่า ออสเตรเลียจุด ค่อนข้างมาก</span></span><br />\r\n<br />\r\n<span style=\"font-size:16px\"><strong><span style=\"background-color:#DDA0DD\">- ออสเตรเลียจุด (Australian Spot Arowana)</span></strong></span><span style=\"font-size:14px\"><strong><span style=\"background-color:#FFFFE0\">&nbsp;</span></strong><span style=\"background-color:#FFF0F5\">ส่วนเจ้านี่เราเจอกันบ่อยจนแทบซี้กันเลยทีเดียว ปลามังกรออสเตรเลียจุด (ขอเรียกว่า &ldquo;ออสเตรเลีย&rdquo; เฉยๆ นะครับ) ลักษณะโดยทั่วไปของสายพันธุ์นี้ก็คือหัวจะเป็นทรงปลาทู ทู่สั้น ตัวจะออกยาว ไม่หนา หนวดสั้นมากและงองุ้ม หาง ครีบและเกล็ดจะเล็กกว่าของสาย Asian (ปลา Asian Arowana แถวเกล็ดจะมี 6 แถวแต่สำหรับของออสเตรเลียจะมี 8 แถว) มีจุดสีส้มๆ ขึ้นตามลำตัว หางและครีบ เรียกว่า&nbsp;</span><strong><span style=\"background-color:#FFF0F5\">&ldquo;Orange Spot&rdquo;</span></strong><span style=\"background-color:#FFF0F5\">&nbsp;จะเด่นชัดมากเมื่อปลาโตขึ้น</span><br />\r\n<br />\r\n<span style=\"background-color:#FFF0F5\">จุดด้อยของปลาสายพันธุ์นี้คือ &ldquo;เกล็ด&rdquo; จะเล็กและ &ldquo;หนวด&rdquo; ก็ยังงุ้มเข้าหาปากไม่ยื่นออกเรียวยาวเหมือนกับสายพันธุ์อื่นจึงดูไม่สง่างาม ปลาออสเตรเลียในขนาด 6&rdquo;-12&rdquo; สีตัวจะเป็นสีทองอร่ามสวยมาก แต่หลังจากนั้นจะเริ่มจางลงและเมื่อปลาใหญ่เต็มที่แล้วความเงางามจะหายไป มังกรออสเตรเลียก็เป็นอีกสายพันธุ์นะครับที่เหมาะสำหรับการเริ่มต้นเลี้ยงปลามังกร</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164534.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:330px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164546.jpg\" style=\"border-style:solid; border-width:0px; height:250px; width:330px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><span style=\"font-size:22px\"><strong><span style=\"background-color:#F0F8FF\">3. African Arowana</span></strong> <span style=\"background-color:#F0F8FF\">มีเพียงสายพันธุ์เดียวคือ</span></span></p>\r\n\r\n<p><span style=\"color:#8B4513\"><strong><span style=\"font-size:16px\"><span style=\"background-color:#FFFFE0\">- อโรวาน่าอัฟริกา</span></span><span style=\"background-color:#FFFFE0\">&nbsp;</span></strong></span><span style=\"background-color:#FFF0F5\">ในประเทศไทยมีขายปลามังกรสายพันธุ์นี้น้อยมากเพราะฉะนั้นจึงไม่เป็นที่นิยมเลี้ยง เหตุผลนั่นไม่ได้เพราะหายากนะครับแต่เพราะปลาพันธุ์นี้มีความสวยงามที่ไม่ค่อยโดดเด่นนัก สีสันไม่เด่นชัด พื้นเกล็ดเป็นสีเทา หัวทู่ ไม่มีหนวด เกล็ดเล็ก ครีบทุกครีบสั้นหมดดูไม่สง่างาม (ปัจจุบันหาซื้อได้ที่ร้านขายปลาแปลกทั่วๆ ไป สนนราคาไม่แพงนัก สามารถซื้อหามาเลี้ยงได้โดยไม่ลำบากครับ)</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"รูปภาพที่เกี่ยวข้อง\" src=\"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWyaZgIwoy9Paxn4x2PAo-cQha107F5To1dwIbRRZ0WkSZOF_I\" style=\"border-style:solid; border-width:5px; height:300px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:18px\"><span style=\"color:#FFA500\">ในภาพ : ปลาอโรวาน่าเเอฟฟริกาขนาดโตเต็มไว</span></span></strong></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><span style=\"font-size:22px\"><strong><span style=\"background-color:#F0FFFF\">4.</span> <span style=\"background-color:#F0FFFF\">South</span> <span style=\"background-color:#F0FFFF\">America</span> <span style=\"background-color:#F0FFFF\">Arowana</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"color:#696969\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FAEBD7\">- อโรวาน่าเงิน</span> <span style=\"background-color:#FAEBD7\">(Silver Arowana)</span></strong></span></span> <span style=\"background-color:#FFF0F5\">ปลามังกรเงินมีถิ่นกำเนิดบริเวณเขตลุ่มน้ำอเมซอนในอเมริกาใต้ ปลาในวัยเล็กจะเป็นสีเงินอมชมพู ครีบหางและครีบก้นปลายขอบมีสีชมพู หนวดยาวสวยเด่น ชายน้ำหรือที่เรียกกันว่า &ldquo;ครีบก้น&rdquo; ยาวมาก เมื่อปลาโตขึ้นจนได้ขนาด สีเกล็ดจะเป็นสีเงินเงางามแต่ขอบครีบที่เป็นสีชมพูดจะจางหายไป</span><br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"background-color:#FFF0F5\">มังกรเงินมีความยาวสูงสุดในหมู่ปลามังกรสวยงามที่นิยมเลี้ยงทั่วไป (ไม่รวมอะราไพม่า) เพราะเมื่อโตเต็มที่จะยาวได้ถึง 1 เมตร 20 เซนเลยทีเดียว แม้ตัวจะใหญ่ได้ถึงขนาดนี้แต่เจ้านี่ก็อ่อนแอมากเลี้ยงกับใครก็มักจะเป็นผู้ถูกทำร้ายไปซะหมด (สู้เขาไม่ค่อยได้) ในมังกรเงินขนาดใหญ่ผู้เลี้ยงมักจะเจอปัญหาเรื่อง &ldquo;ตาตก&rdquo; รุนแรงและปากงุ้มน่าเกลียด ซึ่งส่วนหนึ่งก็คงเพราะลีลาในการกินอันรุนแรงตามนิสัยของมัน แต่ด้วยส่วนกรามที่บอบบางไม่ค่อยแข็งแรงนักแล้วกลับกินอาหารอย่างดุเดือดจึงมีโอกาสให้กรามหักและปากงุ้มเสียรูปได้ ยังไงก็ตามสำหรับปลามังกรสายพันธุ์ที่คนนิยมเลี้ยงดูเล่น เจ้านี่ก็ยังคงครองความเป็นหนึ่งจนถึงทุกวันนี้ครับด้วยราคาที่ถูกมาก (ถูกที่สุดในตระกูลปลามังกรทั้งหมด) เลี้ยงง่าย โตเร็ว และเชื่องน่ารัก แล้วใครล่ะที่จะปฏิเสธลง</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164554.jpg\" style=\"border-style:solid; border-width:5px; height:320px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- อโรวาน่าดำ</span> <span style=\"background-color:#FFFFE0\">(Black Arowana)</span></strong> </span><span style=\"font-size:14px\"><span style=\"background-color:#FFF0F5\">ในวัยเล็กมีลักษณะคล้ายมังกรเงินแต่ว่าเป็นสีดำ ปลามังกรดำเลี้ยงยากกว่ามังกรเงินมาก ตายง่าย กินยาก อ่อนแอไม่ค่อยแข็งแรง แม้ปลาวัยเล็กตัวจะเป็นสีดำแต่พอโตขึ้นสีจะค่อยๆ ถอดออกเป็นคล้ำดูคล้ายกับมังกรเงิน ปลามังกรดำโตเต็มที่ความยาวประมาณ 20&rdquo; และกว้างไม่เกิน 5&rdquo; ซึ่งถือว่าเล็กมากหากเทียบกับพันธุ์อื่นๆ มังกรดำก็เป็นอีกสายพันธุ์นึงที่เหมาะสำหรับผู้เริ่มเลี้ยงเพราะราคาไม่แพงแต่ว่าอาจจะหาซื้อยากหน่อยเพราะมีขายกันเป็นฤดูเท่านั้น</span><span style=\"background-color:#FFFFE0\"><span style=\"background-color:#FFF0F5\">​</span></span></span><br />\r\n<br />\r\n<br />\r\n<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164568.jpg\" style=\"border-style:solid; border-width:5px; height:300px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p><br />\r\n<span style=\"color:#FF8C00\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFF0F5\">- อะราไพม่า</span> <span style=\"background-color:#FFF0F5\">(Arapima Gigas)</span></strong> <span style=\"background-color:#FFF0F5\">หรือที่เรียกว่า</span> <span style=\"background-color:#FFF0F5\">&ldquo;ปลาช่อนอเมซอน&rdquo;</span></span></span><span style=\"font-size:14px\"> <span style=\"background-color:#FFF0F5\">ลักษณะของปลาชนิดนี้คือเล็กๆ ตัวจะเป็นสีเขียวและหางสีดำ แต่พอโตขึ้นตัวจะมีสีเขียวเข้มเป็นมรกตแต่ไม่เงางามและส่วนหางจะมีปื้นแดงส้มเป็นลายคาดเรียกว่า &ldquo;กุหลาบไฟ&rdquo; อะราไพม่าเป็นปลาเกล็ดเล็กที่สุดในตระกูลปลามังกร แถมยังไม่มีหนวดดูแล้วเป็นปลาอโรวาน่าที่หน้าตาไม่เหมือนเพื่อนพ้องเอาเสียเลย ตัวก็ใหญ่โตมโหฬารโดยโตเต็มที่ได้ถึง 3 เมตร</span>&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;<span style=\"background-color:#FFF0F5\">ปลาอะราไพม่าโดยปกติจะเลี้ยงลงบ่อ น้อยคนนักจะเลี้ยงในตู้เนื่องจากว่าเป็นปลาที่โตเร็วมาก ในระยะเวลา 1 ปี ปลามังกรทั่วไปขนาด 6 นิ้วจะโตได้ราว 12-15 นิ้ว ในขณะที่อะราไพม่าจะโตได้ถึง 30-45 นิ้ว และเป็นปลาที่แข็งแรงมากจึงยากต่อการเคลื่อนที่ย้ายเข้าย้ายออกบ่อยๆ นอกจากนี้การว่ายน้ำของอะราไพม่ายังว่ายอย่างช้าๆ และไม่เป็นทิศเป็นไม่ทางเหมือนปลามังกรทั่วไปที่ว่ายกลับไปกลับมาเป็น &ldquo;หยินหยาง&rdquo; มีทิศมีทางดูสง่างามกว่า แต่สำหรับคนที่มีสถานที่และชอบความยิ่งใหญ่ อะราไพมายังเป็นปลายักษ์ในอันดับต้นๆ ที่คนเหล่านั้นนิยมซื้อหามาเลี้ยง</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"รูปภาพที่เกี่ยวข้อง\" src=\"http://o9.dq1.me/img/user-14-2016-06-15-15-37-42.jpeg?d=ontopof.com\" style=\"border-style:solid; border-width:5px; height:300px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#FF0000\"><strong><span style=\"background-color:#F0FFF0\">นอกจากนี้ยังมีสายพันธุ์ที่เกิดขึ้นมาใหม่ แต่ยังไม่ได้ถูกยอมรับตามมาตรฐานซึ่งก็มีดังนี้</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#FFD700\"><strong><span style=\"background-color:#FFFFE0\">- Golden Red Arowana</span></strong></span></span>&nbsp;<span style=\"background-color:#FFF0F5\">ถือเป็นปลาแดงเกรดรองซึ่งลักษณะเกล็ดจะเป็นสีทองอมแดง ส่วนหางและครีบทั้งหมดเป็นสีแดงสดโอกาสจะที่จะพัฒนามาเป็นแดงเต็มตัวมีน้อยมาก ปลา Super Red ทั่วๆ ไปตามท้องตลาดที่เกรดราคา 20,000 กลางๆ ส่วนใหญ่โตขึ้นมาแล้วมักจะเป็น Golden red หรือไม่ก็ Orange Red&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#00FF00\">***</span> &nbsp; &nbsp;<span style=\"background-color:#FFF0F5\">(ในบางเมืองที่ประเทศอินโดนีเซีย หากกล่าวถึง Golden Red แล้ว ก็จะหมายถึง Red Tail Golden หรือ ทองอินโด ครับ)</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164604.jpg\" style=\"border-style:solid; border-width:0px; height:300px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#A52A2A\"><strong><span style=\"background-color:#FFFFE0\">- Hiback</span> <span style=\"background-color:#FFFFE0\">Arowana</span></strong></span></span><span style=\"font-size:14px\"><span style=\"color:#000000\">&nbsp;<span style=\"background-color:#FFF0F5\">เป็นลูกผสมระหว่างทองมาเลย์กับทองอินโด เพื่ออัพเกรดสายพันธุ์ทองอินโดให้สูงขึ้น ลักษณะปลา Hiback คือเกล็ดปลาจะเปิดสูงขึ้น จากเดิม &ldquo;ทองอินโด&rdquo; ที่ได้แค่เพียงแถวที่ 4 อาจขึ้นมาถึงแถวที่ 5 แต่อย่างไรก็ตามสีเกล็ดจะไม่ข้ามหลัง นอกจากจะแตกต่างกันที่เรื่องเปิดสูงไม่สูงแล้ว Base สีก็ยังเป็นจุดสำคัญที่ใช้มองความความแตกต่างระหว่าง Hiback กับทองอินโด เพราะโดยปกติแล้ว Hiback ที่เป็นลูกผสมทองมาเลย์กับทองอินโดจริงจะมีสีเข้มกว่า สีครีบจะเป็นโทนเข้ม ไม่ว่าจะเป็นสีเลือดหมูหรือสีแดง ปัจจุบันมีตลาดให้การต้อนรับสายพันธุ์ Hiback มากเพราะว่าราคาสูงกว่าทองอินโดไม่มากแต่เมื่อโตมาแล้วดูสวยงามกว่า (ปลาอโรวาน่าทองมาเลย์ที่ไม่สวยหรือทองอินโดที่สวยๆ อาจถูกพ่อค้าหัวใสใจกล้าเอามาขายเป็น Hiback ได้</span><span style=\"background-color:#FFFFE0\">)</span><br />\r\n<br />\r\n<span style=\"background-color:#00FF00\">***</span> <span style=\"background-color:#FFF0F5\">ปัจจุบัน Hiback Arowana เป็นที่นิยมค่อนข้างมาก และมีการแตกย่อยในอีกหลายชื่อ ตัวอย่างเช่น Hiback Golden, Mahoto Golden, Super Hiback, Premium Grade Red Tail Gold และอื่นๆ อีกมากมาย สนใจจะเป็นเจ้าของซักตัวขอให้ขยันอ่าน และหาเวลาเดินดูปลาให้มากๆ ก่อนนะครับ ของมีมาก ทางเลือกก็มาก ทางลวงก็มีมากด้วยเช่นกัน ถ้าไม่ต้องการจ่ายแพงกว่าก็ต้องศึกษาให้มากๆ ครับ ทิ้งท้ายไว้นิดหนึ่ง&nbsp;</span><u><span style=\"background-color:#FFF0F5\">ในต่างประเทศ Hiback Arowana ถูกจัดให้อยู่ในระดับชั้นของ Red Tail Golden - RTG Class คือ ทองอินโด</span></u><span style=\"background-color:#FFF0F5\">&nbsp;(ไม่อยู่ในทองมาเลย์เซีย)</span><span style=\"background-color:#FFFFE0\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164655.jpg\" style=\"border-style:solid; border-width:5px; height:300px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"color:#008000\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- Indo</span> <span style=\"background-color:#FFFFE0\">Green</span></strong></span></span><span style=\"font-size:14px\">&nbsp;<span style=\"background-color:#FFF0F5\">เป็นอีกหนึ่งลูกผสมที่มาจากสายพันธุ์ทองอินโดแต่มีคุณภาพต่ำกว่า นั่นก็คือลูกผสมระหว่างสายพันธุ์ ทองอินโด และ มังกรเขียว ซึ่งในวัยเล็กจะมีจุดเด่นเรื่องการเปิดของเกล็ด (ต่างจาก Hiback สายแท้ที่เป็นลูกผสมทองอินโด กับ ทองมาเลย์) คือจะเปิดสูงมากๆ เปิดระเบิดเถิดเทิงแบบที่เห็นแถวที่ 5 เรียงเป็นแถบตั้งแต่เล็ก แต่หากสังเกตดีๆ วงเกล็ดข้างในจะติดเนื้อเขียวเข้ามาด้วย แม้ความโดดเด่นจะอยู่ที่การเปิด แต่เรื่องสีสันก็ถือเป็นจุดด้อยที่ต้องทำให้ผู้เลี้ยงต้องย้ำคิดพิจารณาหากจะคิดหามาครอบครอง นั่นก็คือ เปิดสูงอย่างเดียวแต่สีสันไม่ค่อยชัดเจน</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164670.jpg\" style=\"border-style:solid; border-width:5px; height:320px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- Tong</span> <span style=\"background-color:#FFFFE0\">Yan</span> <span style=\"background-color:#FFFFE0\">(ถงเยิ่น)</span></strong></span></span><span style=\"font-size:14px\"> <span style=\"background-color:#FFF0F5\">เป็นลูกผสมระดับชั้นสูงสุดที่ถูกยอมรับในเรื่องคุณภาพของสายพันธุ์จากผู้เลี้ยงในปัจจุบัน เพราะเป็นลูกผสมระหว่าง Super Red และ ทองมาเลย์ (ใบรับรองสายพันธุ์ส่วนใหญ่ถ้าไม่มีชื่อใหม่เป็นพิเศษจะระบุว่า Xback X Super Red) ลักษณะของสีสันที่ได้มาบ่งชี้ชัดว่าเป็นลูกครึ่งขอ</span></span><a href=\"javascript:void(\'%E0%B8%95%E0%B8%B1%E0%B8%94\')\" id=\"cke_50\" onclick=\"CKEDITOR.tools.callFunction(164,0); return false;\" tabindex=\"-1\" title=\"ตัด\">ตัด</a><span style=\"font-size:14px\"><span style=\"background-color:#FFF0F5\">งทั้งสองสายพันธุ์คือออกเป็น &ldquo;สีทองแดง&rdquo; บางตัวได้ความโดดเด่นจากแดงมากหน่อย ก็จะเป็นสีสนิมเข้ม แก้มสีแดง ตาสีแดง บางตัวได้คุณสมบัติจากทองมาเลย์ ก็จะมีการตัดขอบเกล็ดด้วยสีสันหลายมิติ ดูสวยงาม อีกทั้งมีการเปิดของเกล็ดละเอียด และข้ามหลังด้วยเช่นกัน</span><span style=\"background-color:#FFFFE0\">&nbsp;</span><br />\r\n<br />\r\n<span style=\"background-color:#00FF00\">***</span> <span style=\"background-color:#FFF0F5\">Tongyan ในเมืองไทยชื่ออาจจะยังไม่เป็นที่รู้จัก ไม่เคยได้ยิน แต่หากบอกว่าเป็น Red Splendor จะคุ้นหูกันมากกว่า และ&nbsp;</span><u><span style=\"background-color:#FFF0F5\">ในต่างประเทศปลาสายพันธุ์นี้ถูกจัดให้อยู่ในระดับชั้นของ Malaysian Golden Cross Back คือ ทองมาเลเซีย</span></u><span style=\"background-color:#FFF0F5\">&nbsp;(ไม่อยู่ในปลาแดง) และมีชื่อพิเศษที่เป็นที่ทราบกันว่า Tong Yan Cross Back&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164694.jpg\" style=\"border-style:solid; border-width:5px; height:355px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>ในภาพ : เป็นปลาลูกผลผสมระหว่างซุปเปอร์ เรด กับ ทองมาเลย์&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#cccc66\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- ปลามังกรทองอ่อน</span></strong></span></span><span style=\"font-size:14px\">&nbsp;<span style=\"background-color:#FFF0F5\">ได้รับรู้ข้อมูลของเจ้ามังกรเขียวไปแล้วคราวนี้มาดูตัวที่เป็นฝาแฝดกันมั่งครับ สำหรับเจ้าทองอ่อนตัวจะเป็นสีทองอ่อนๆ โตขึ้นบริเวณริมหาง ครีบหลังและครีบก้นจะด่างเป็นสีขาว พวกทองอ่อน มังกรเขียว มังกรเงิน มังกรดำ ถือเป็นปลา Base สีเดียวนะครับคือเหมือนๆ กันทั้งตัวต่างกับสายพันธุ์อื่นที่มีสันสันมากกว่า ผู้เลี้ยงปลาชนิดนี้ครั้งแรกนิยมเลี้ยงปลาพันธุ์นี้ก่อนที่จะเลี้ยงปลาแพงอย่างทองมาเลย์ แดง และทองอินโด เพราะราคาไม่แพงมาก</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164703.jpg\" style=\"border-style:solid; border-width:5px; height:350px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\"><span style=\"color:#cc9999\">- Yellow</span></span><span style=\"color:#cc9999\"> </span><span style=\"background-color:#FFFFE0\"><span style=\"color:#cc9999\">Tail</span></span></strong></span><span style=\"font-size:14px\"><strong>&nbsp;</strong><span style=\"background-color:#FFF0F5\">เท่าที่ผมทราบและเป็นข้อมูลทางการตามมาตราฐานสากลจากจากเวบไซท์ต่างประเทศ ปลามังกร Yellow Tail เป็นหนึ่งใน 3 สายพันธุ์ในระดับชั้น Red B ซึ่งแบ่งออกเป็น (Yellow Tail (ทองหางเหลือง) / Red 1.5 (ทองหางส้ม) และ Banjar Red (ทองหางแดง) แต่อยู่ในระดับราคาที่ต่ำสุด และโดยรวมมีลักษณะคล้ายคลึงมังกรเขียวมากที่สุด แต่ในความเป็นจริง ระดับชั้นของ Banjar Red ทั้งหมดนั้น หน้าตาจะคล้ายคลึงกับมังกรเขียว และทองอ่อนมาก โดยเฉพาะสีเกล็ด และสีของลำตัว จะต่างกันก็ตรงที่สีหาง (ที่ถ้าเป็น ทองอ่อน หรือ เขียว จะเป็นสีขาว และเทา แต่ถ้าเป็น Red B จะเป็น เหลือง ส้ม แดง ตามที่กล่าวมา) แต่สุดท้ายปลายทาง ในระดับชั้นที่ใกล้เคียงกันนี้ที่ &quot;ขอบหาง&quot; ของทุกตัวจะมีสีขาวให้เห็นครับ</span></span></p>\r\n\r\n<p><span style=\"color:#990033\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- Super</span> <span style=\"background-color:#FFFFE0\">Red</span> <span style=\"background-color:#FFFFE0\">Grade</span> <span style=\"background-color:#FFFFE0\">B</span> <span style=\"background-color:#FFFFE0\">(Banja</span> <span style=\"background-color:#FFFFE0\">Red)</span></strong></span></span><span style=\"font-size:14px\"><strong><span style=\"background-color:#FFFFE0\">&nbsp;</span></strong><span style=\"background-color:#FFF0F5\">ในปลาวัยเล็กจะดูคล้ายคลึงกับปลาแดงมาก แต่หากสังเกตดีๆ สีหางและครีบของ Red B จะเป็นสีส้มในขณะที่ปลาแดงเกรด A จะเป็นสีแดงสดและเต็มใบ เหลือบสีก็เช่นกัน (สีของเกล็ดเวลาที่กระทบกับแสง) ของ Red B จะออกเป็นสีม่วงอ่อนๆ เงา หรืออาจจะไม่มีดูแล้วแค่ขาวๆ เงาๆ เท่านั้น แต่ถ้าเป็น Red A เหลือบสีจะเห็นเป็นสีม่วงชัดเจน Red B เมื่อโตเต็มที่แล้วจะมีลักษณะสีสันจะคล้ายทองอ่อน แต่หางและครีบจะออกเป็นสีส้มเข้ม</span><br />\r\n<br />\r\n<span style=\"color:#990099\"><strong>***</strong>&nbsp;<span style=\"background-color:#FFF0F5\">เพื่อเป็นการเพิ่มช่องการการตลาดให้ขายจำหน่ายปลาได้ง่ายขึ้น ปัจจุบันฟาร์มปลาและร้านค้าได้เปลี่ยนชื่อในใบรับรองสายพันธุ์ของปลา Banjar Red เป็น Red Arowana หรือ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">มังกรแดง</span></u><span style=\"background-color:#FFF0F5\">&rdquo; ดังนั้น ขอให้เพื่อนผู้อ่านทุกท่านโปรดเข้าใจว่า&nbsp;</span><u><span style=\"background-color:#FFF0F5\">Red Arowana คือ Red B</span></u><span style=\"background-color:#FFF0F5\">&nbsp;นะครับ เพราะมีหลายท่านเข้าผิดคิดว่าเป็น Super Red Grade A</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223216003.jpg\" style=\"border-style:solid; border-width:5px; height:350px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"color:#660033\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFF0F5\">- King</span> <span style=\"background-color:#FFF0F5\">Arowana</span></strong></span></span><span style=\"font-size:14px\"><span style=\"color:#000000\"> <span style=\"background-color:#FFF0F5\">ปลาอโรวาน่าชนิดนี้คือปลาพิการในส่วนของ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">ช่วงหลังและคอ</span></u><span style=\"background-color:#FFF0F5\">&rdquo; ทำให้ดูค่อมๆ หลังจะสูงเกินส่วนหัวขึ้นมามาก แม้เจ้านี่จะเรียกได้ว่าเป็นปลาพิการแต่ King สวยๆ นี่พิการแล้วแต่ออกมาดูดีนะครับ King Arowana เป็นได้ทุกสายพันธุ์ตั้งแต่ถูกจนแพง (ส่วนในมังกรเงินมีลักษณะพิเศษใหม่ด้วยก็คือ &ldquo;คอ V&rdquo; แปลกดีเหมือนกัน) ราคาของปลา King ก็จะแพงมากด้วยยิ่งถ้าเป็นปลาระดับสูงอย่างปลาแดงหรือทองมาเลย์ King ตัวนึงนี่ว่ากันเป็นแสนบาทเลยทีเดียว</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1223164712.jpg\" style=\"border-style:solid; border-width:5px; height:355px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#B22222\"><span style=\"font-size:18px\">ในภาพ : เป็นปลาที่พิการ หลังจะสูงเกินกว่าหัวขึ้นมามากทำให้ดูเหมือนเป็นมงกุฏ จึงได้ขึ้นชื่อว่าเป็น&quot;King Arowana&quot;</span></span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"color:#006699\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- Albino</span></strong></span></span><span style=\"font-size:14px\"> <span style=\"background-color:#FFF0F5\">ลักษณะพิเศษของปลาแปลกชนิดนี้ก็คือ &ldquo;</span><u><span style=\"background-color:#FFF0F5\">เผือกตาแดง</span></u><span style=\"background-color:#FFF0F5\">&rdquo; ตัวปลาจะมีสีเหลืองอ่อนๆ และตาสีแดง Albino เป็นสายพันธุ์ที่เกิดจากการ Breed สายพันธุ์กันเองระหว่างครอบครัวเดียวกันจนเป็นสายพันธุ์ที่ด้อยลง ในเมืองไทยยังไม่มีใครนำเข้ามานะครับเพราะในโลกนี้มีไม่กี่ตัวเท่านั้นเอง อีกอย่างราคาสนนกันเป็นล้านครับ คงต้องใจถึงพอตัวจึงจะได้ครอบครอง</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.aro4u.com/forums/uploads/post-2-1242828559.jpg\" style=\"border-style:solid; border-width:5px; height:300px; width:600px\" /></p>\r\n\r\n<hr />\r\n<p><span style=\"color:#ff33cc\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#FFFFE0\">- Platinum</span></strong></span></span><span style=\"font-size:14px\"><strong> </strong><span style=\"background-color:#FFF0F5\">ปลา Platinum ถือเป็นปลาแปลกชนิดนึง ทั้งตัวจะเป็นสีเงินและเงา (แต่ไม่ใช่สีเงินเหมือนมังกรเงินนะครับ) Platinum เขียวจะดูเด่นที่สุดคือทั้งตัวจะเป็นสีเดียวกันเลยครับทั้งหัว ครีบ เกล็ดและหางจะเป็นสีขาวเงาทั้งหมด (ยกเว้นลูกตา) ปลา Platinum เกิดกับปลาตระกูลหลักได้ทุกสายพันธุ์ตั้งแต่ Platinum ของมังกรเขียว Platinum ของทองอินโด Platinum ของแดงหรือแม้แต่ของทองมาเลย์ และเพราะเป็นปลาแปลกหายากราคาที่เสนอขายจึงแพงมาก ในบ้านเราไม่ค่อยมีให้เห็นเพราะผู้คนไม่ค่อยนิยมเลี้ยงและเพราะเหตุนั้นจึงมีร้านค้าน้อยรายมากที่กล้านำเข้ามาจำหน่าย (เกรงว่าจะขายไม่ออก)</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Click the image to open in full size.\" src=\"http://www.aro4u.com/forums/uploads/post-2-1242827933.jpg\" style=\"height:500px; width:700px\" title=\"Click the image to open in full size.\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><span style=\"color:#008000\">Credit :&nbsp;<a href=\"https://board.postjung.com/638222.html\">https://board.postjung.com/638222.html</a></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\">มีต่อ...</span></p>\r\n', 7, '17/05/2017 00:41:09', '');
INSERT INTO `webboard` (`id`, `posted_by`, `title`, `detail`, `views`, `date_create`, `date_modify`) VALUES
(6, 1, '10 อันดับ ปลาสวยงามของไทยที่สวยที่สุด', '<p style=\"text-align:center\"><img src=\"http://www.wallpapers13.com/wp-content/uploads/2016/02/Astronotus_ocellatus-Hd-wallpaper-710x400.jpg\" title=\"Astronotus Ocellatus Hd Wallpaper\" /></p>\r\n\r\n<h2 style=\"text-align:center\"><span style=\"color:#0000FF\"><span style=\"font-size:18px\"><strong><span style=\"background-color:#E6E6FA\">เอาใจคนรักปลากันหน่อย เราจัดอันดับปลาของไทยที่สวยงามที่สุด มาดูกันว่าตรงกับที่คุณคิดหรือเปล่าเรามาเริ่มนับตั้งเเต่อันดับ10เลยดีกว่าครับ :)</span></strong></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลากระเบนเสือดาว\" src=\"http://phuketaquarium.org/wp-content/uploads/2015/03/19.jpg\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 10 ปลากระเบนเสือดาว</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลากระเบนลายเสือ (อังกฤษ: Marbled whipray) เป็นปลากระเบนน้ำจืดชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Himantura oxyrhynchus อยู่ในวงศ์ปลากระเบนธง (Dasyatidae) รูปร่างเหมือนปลากระเบนชนิดอื่นในวงศ์เดียวกัน หางยาว โคนหางมีเงี่ยงแหลมมีพิษ 1 หรือ 2 ชิ้น ที่สามารถงอกใหม่ได้เมื่อหลุดหรือหักไป หางไม่มีริ้วหนัง พื้นลำตัวด้านบนสีน้ำตาลเหลือง กลางหลังมีเกล็ดเป็นตุ่มหยาบ ๆ มีจุดดำคล้ายลายของเสือดาวกระจายอยู่ทั่วตัวไปจนปลายหาง อันเป็นที่มาของชื่อ พื้นลำตัวด้านล่างสีขาว หากินตามพื้นท้องน้ำโดยอาหารได้แก่ ปลาขนาดเล็ก, สัตว์หน้าดิน และสัตว์มีเปลือก จะว่ายขึ้นมาหากินบริเวณผิวน้ำบ้างเป็นบางครั้ง มีขนาดประมาณ 40 เซนติเมตร ปลากระเบนลายเสือเป็นปลาน้ำกร่อยที่พบอาศัยอยู่ค่อนมาทางน้ำจืด เป็นปลาที่พบน้อย พบได้ตามปากแม่น้ำ เช่น ปากแม่น้ำเจ้าพระยา, ปากแม่น้ำโขง, ทะเลสาบเขมร และพบได้ไกลถึงปากแม่น้ำบนเกาะบอร์เนียวของอินโดนีเซีย เป็นต้น แต่มีรายงานทางวิชาการว่าพบครั้งแรกที่ แม่น้ำน่าน เนื่องจากเป็นปลาที่มีลวดลายสวยงามจึงนิยมเลี้ยงเป็นปลาสวยงามที่มีราคาแพง แต่มักจะเลี้ยงไม่ค่อยรอดเพราะปลามักประสบปัญหาปรับตัวให้อยู่ในน้ำจืดหรือภาวะแวดล้อมในที่เลี้ยงไม่ค่อยได้</span></span></p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลากระแหทอง\" src=\"http://www.t3udon.ac.th/webdream/ex/02/images/fish09.jpg\" style=\"border-style:solid; border-width:2px; height:250px; width:450px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 9 ปลากระเเหทอง</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ชื่อสามัญ : Schwanenfeld&#39;s Tinfoil Barb ชื่อวิทยาศาสตร์ : Barbodes schwanenfeldi ลักษณะทั่วไป :เป็นปลาน้ำจืดที่มีรูปร่างป้อมสั้น ลำตัวแบนข้าง หัวเล็ก จะงอยปากสั้นทู่ นัตย์ตาเล็ก ปากเล็กและอยู่ปลายสุด หนวดเล็กและสั้นมี 2 คู่ เกล็ดมีขนาดใหญ่ ครีบกระโดงหลังสูง และกว้างมีสีแดง ลำตัวเป็นสีขาวเงินและสีเหลืองปนส้ม ด้านหลังสีเทาปนเขียว แก้มสีเหลืองปนแดง ขนาดของลำตัวความยาว 15 - 35 เซนติเมตร นิสัย :รักสงบ อยู่รวมกันเป็นฝูง ปราดเปรียว ว่องไว ไม่อยู่นิ่งชอบว่ายน้ำตลอดเวลา ถิ่นอาศัย :พบทุกภาคในประเทศไทย ทำให้มีชื่อเรียกแตกต่างกันออกไป (ทะเลสาบสงขลาตอนใน) แม่น้ำตาปี ชาวใต้เรียกชื่อปลานี้ว่าปลากระทิงลายดอกไม้ อาหาร : พืชพันธุ์ไม้น้ำ ตัวอ่อนแมลงน้ำ ซากสัตว์และพืชที่เน่าเปื่อย</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><br />\r\n<img alt=\"ผลการค้นหารูปภาพสำหรับ ปลาตองลาย\" src=\"data:image/jpeg;base64,\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 8&nbsp;ปลาตองลาย</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลาตองลาย (อังกฤษ: Royal Knifefish) ปลาน้ำจืดชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Chitala blanci อยู่ในวงศ์ปลากราย (Notopteridae) มีรูปร่างเหมือนปลาทั่วไปในวงศ์นี้ แต่มีส่วนหลังและหน้าผากลาดชันน้อยกว่าปลากราย (C. ornata) ซึ่งเป็นปลาที่อยู่ในสกุลเดียวกัน สีลำตัวเป็นสีเงินแวววาว ลำตัวด้านท้ายมีลายจุดและขีดจำนวนไม่แน่นอนคาดเฉียงค่อนข้างเป็นระเบียบ มีขนาดประมาณ 60 เซนติเมตร ใหญ่สุด 1 เมตร เป็นปลาที่พบได้เฉพาะแม่น้ำโขงและลำน้ำสาขาที่ไหลสู่แม่น้ำโขง โดยมีรายงานพบเมื่อปี พ.ศ. 2510 และมีรายงานพบที่แม่น้ำน่านด้วยเมื่อไม่นานมานี้ ซึ่งถือว่าเป็นมีแค่เพียงสองแหล่งนี้ในโลกเท่านั้น เป็นปลาที่หายากชนิดหนึ่ง โดยมีชื่อติดอยู่ในบัญชีแดงของสหภาพเพื่อการอนุรักษ์ธรรมชาติ (IUCN Red List) เมื่อปี พ.ศ. 2537 ด้วย โดยอยู่ในระดับหายาก (R)[1]แต่ปัจจุบันสามารถเพาะขยายพันธุ์ได้แล้ว นิยมเลี้ยงเป็นปลาสวยงาม โดยการเพาะขยายพันธุ์สำเร็จเป็นครั้งแรกที่สถานีประมงน้ำจืดจังหวัดชัยนาท โดยพ่อแม่ปลาเป็นปลาที่จับมาจากแม่น้ำโขง เมื่ออายุประมาณ 1 ปี น้ำหนักประมาณ 100-120 กรัม ใช้เวลาในการเลี้ยงประมาณ 3 ปี ในตู้กระจก จนปลามีความสมบูรณ์เต็มที่เมื่อถึงวัยเจริญพันธุ์พบว่าตัวผู้มีน้ำหนัก 1.8 กิโลกรัม ตัวเมียหนัก 2.1 กิโลกรัม โดยตัวผู้มีความยาวครีบท้องมากกว่าตัวเมียถึงสองเท่า</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><br />\r\n<span style=\"font-size:20px\"><strong>&nbsp; &nbsp; &nbsp;</strong></span><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลาทรงเครื่อง\" src=\"data:image/jpeg;base64,\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 7&nbsp;ปลาทรงเครื่อง</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลาทรงเครื่อง ปลาน้ำจืดชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Epalzeorhynchos bicolor อยู่ในวงศ์ปลาตะเพียน (Cyprinidae) มีรูปร่างคล้ายปลากาดำ (E. chrysophekadion) ซึ่งเป็นปลาในวงศ์และสกุลเดียวกัน แต่มีรูปร่างที่เพรียวยาว มีขนาดเล็กกว่ามาก สีลำตัวสีแดงอ่อน ครีบหางสีแดงเข้ม มีขนาดโตเต็มที่ไม่เกิน 12 เซนติเมตร พบเฉพาะในประเทศไทยเท่านั้น บริเวณแม่น้ำเจ้าพระยาตอนบน ปัจจุบันเชื่อว่า สูญพันธุ์ไปแล้วจากธรรมชาติ (Extinct in the Wild) เนื่องจากถูกคุกคามถิ่นที่อยู่อาศัยและถูกจับไปเป็นปลาสวยงาม ซึ่งปลาที่ขายกันในตลาดปลาสวยงามเป็นปลาที่เกิดจากการผสมเทียมทั้งสิ้น ปลาทรงเครื่อง ยังมีชื่อเรียกอื่น ๆ อีกในวงการปลาสวยงาม เช่น &quot;ฉลามทรงเครื่อง&quot; หรือ &quot;หมูทรงเครื่อง&quot; เป็นต้น</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลากระทิงไฟ\" src=\"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQYVqJ-dsAbJH-GiPEUHE3oVjIi99uCPivn7zVOFczn45CsKltcXg\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 6&nbsp;ปลากระทิงไฟ</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลากระทิงไฟ (อังกฤษ: Fire spiny eel) ปลาน้ำจืดพื้นเมืองของไทยชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Mastacembelus erythrotaenia อยู่ในวงศ์ปลากระทิง (Mastacembelidae) มีรูปร่างเหมือนปลาในวงศ์นี้ทั่วไป กล่าวคือ รูปร่างยาวคล้ายงูหรือปลาไหล แต่ท้ายลำตัวส่วนที่อยู่ค่อนไปทางหางจะมีลักษณะแบนข้าง และส่วนหัวหรือปลายปากจะยื่นยาวและแหลม จะงอยปากล่างจะยื่นยาวกว่าจะงอยปากบน ตามีขนาดเล็ก ครีบหลัง ครีบทวาร และหางจะเชื่อมต่อติดกันเป็นครีบเดียว โดยครีบหลังตอนหน้าจะมีขนาดเล็กมากและลักษณะเป็นหยักคล้ายกับฟันเลื่อย หากไม่สังเกตจะมองไม่เห็น โดยปลายหางมีลักษณะมนโค้ง ปลายค่อนข้างแหลม ไม่มีหนามใต้ตาเช่นปลากระทิงชนิดอื่น ๆ มีหนามแหลมขนาดเล็กตลอดทั้งความยาวลำตัวช่วงบนไว้เพื่อป้องกันตัว ปลากระทิงไฟจะมีรูปร่างป้อมแต่มีขนาดยาวกว่าปลากระทิง (M. armatus) ซึ่งเป็นปลาที่อยู่ในสกุลเดียวกัน มีขนาดเมื่อโตเต็มที่ประมาณ 60 เซนติเมตร พบใหญ่ที่สุดถึง 1 เมตร พบได้ในภูมิภาคเอเชียตะวันออกเฉียงใต้ทุกประเทศจนถึงอินโดนีเซีย สำหรับในประเทศไทยพบได้ในแหล่งน้ำขนาดใหญ่ในภาคกลางและภาคใต้</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลาฉลามหางไหม้\" src=\"http://www.siamensis.org/sites/default/files/imagecache/webboard_preview/balantiocheilos_melanopteru.jpg\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 5&nbsp;ปลาฉลามหางไหม้</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลาหางไหม้ (อังกฤษ: Bala shark) ปลาน้ำจืดชนิดหนึ่งมีชื่อวิทยาศาสตร์ว่า Balantiocheilus ambusticauda ในวงศ์ปลาตะเพียน (Cyprinidae) วงศ์ย่อย Cyprininae - Systomini มีรูปร่างคล้ายปลาตามิน (Amblyrhynchichthys truncatus) มีรูปร่างและทรวดทรงที่เพรียวยาว ตาโต ปากเล็ก ขยับปากอยู่ตลอดเวลา ใต้คางมีแผ่นหนังเป็นถุงเปิดออกด้านท้าย ลำตัวแบนข้างเล็กน้อย เกล็ดมีขนาดเล็กสัดส่วนของครีบทุกครีบเหมาะสมกับลำตัว โดยเฉพาะครีบหางซึ่งเว้าเป็นแฉกลึก สีของลำตัวเป็นสีเงินแวววาว ด้านหลังสีเขียวปนเทา ครีบหลัง ครีบท้อง ครีบก้นและครีบหาง สีส้มแดงและขอบเป็นแถบดำ อันเป็นที่มาของชื่อ ว่ายน้ำได้ปราดเปรียวมาก และกระโดดขึ้นได้สูงจากน้ำมาก มีขนาดโตเต็มที่ไม่เกิน 20 เซนติเมตร นิยมอยู่เป็นฝูง หากินตามใต้พื้นน้ำ ในอดีตพบชุกชุมในเขตที่ราบลุ่มแม่น้ำเจ้าพระยา ในประเทศไทย ในต่างประเทศพบได้ที่เกาะบอร์เนียว ประเทศอินโดนีเซีย (ปลาที่พบในอินโดนีเซียสีของครีบหางจะออกเหลืองสดกว่า) แต่สถานภาพในปัจจุบัน ในประเทศไทยได้สูญพันธุ์ไปแล้วจากธรรมชาติ ในอินโดนีเซียก็ใกล้จะสูญพันธุ์เช่นกัน</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลากระดี่มุก\" src=\"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTx5XpfcfDzBYOvQgRwLG4f8TBNgc178g7_n4dlY2dT3B0uG_w8\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 4&nbsp;ปลากระดี่มุก</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลากระดี่มุก ปลาน้ำจืดชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Trichogaster leeri ในวงศ์ปลากัด ปลากระดี่ (Osphronemidae) มีรูปร่างคล้ายปลากระดี่หม้อ (T. trichopterus) ซึ่งเป็นปลาที่อยู่ในสกุลเดียวกัน แต่กระดี่มุกมีลำตัวกว้างกว่าเล็กน้อย ครีบหลัง ครีบหาง และครีบก้นมีขนาดใหญ่และมีก้านครีบอ่อนยาวเป็นเส้นริ้ว ลำตัวสีเงินจาง มีแถบสีดำจางพาดยาวไปถึงโคนครีบหาง ท้องมีสีส้มหรือสีจาง และมีจุดกลมสีเงินมุกหรือสีฟ้าเหลือบกระจายไปทั่ว อันเป็นที่มาของชื่อ &quot;กระดี่มุก&quot; ครีบท้องเป็นสีส้มสดหรือสีเหลือง มีความยาวเต็มที่เฉลี่ย 10-12 เซนติเมตร มีพฤติกรรมมักอาศัยอยู่เป็นคู่หรือกลุ่มเล็ก ๆ ในแหล่งน้ำที่มีค่าของน้ำมีความเป็นกรดต่ำกว่าค่าของน้ำปกติ (ต่ำกว่า 7.0) เช่น ในป่าพรุ เป็นต้น เป็นปลาจำพวกปลากระดี่ที่พบในธรรมชาติได้น้อยที่สุดในประเทศไทย โดยจะพบในเฉพาะพื้นที่ภาคกลางและภาคใต้ตอนล่างเท่านั้น นิยมเลี้ยงเป็นปลาตู้สวยงาม โดยเฉพาะในตู้ไม้น้ำ</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลากัดไทย\" src=\"http://www.rook100.com/webboard/index.php?action=dlattach;topic=47.0;attach=15763;image\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 3&nbsp;ปลากัดไทย</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลากัดภาคกลาง หรือที่นิยมเรียกสั้น ๆ ว่า ปลากัด เป็นปลาน้ำจืดขนาดเล็ก มีชื่อวิทยาศาสตร์ว่า Betta splendens อยู่ในวงศ์ Macropodinae ซึ่งอยู่ในวงศ์ใหญ่ Osphronemidae มีรูปร่างเพรียวยาวและแบนข้าง หัวมีขนาดเล็ก ครีบก้นยาวจรดครีบหาง หางแบนกลม มีอวัยวะช่วยหายใจบนผิวน้ำได้โดยใช้ปากฮุบอากาศโดยไม่ต้องผ่านเหงือกเหมือนปลาทั่วไป เกล็ดสากเป็นแบบ Ctenoid ปกคลุมจนถึงหัว ริมฝีปากหนา ตาโต ครีบอกคู่แรกยาวใช้สำหรับสัมผัส ปลาตัวผู้มีสีน้ำตาลเหลือบแดงและน้ำเงินหรือเขียว ครีบสีแดงและมีแถบสีเหลืองประ ในขณะที่ปลาตัวเมียสีจะซีดอ่อนและมีขนาดลำตัวที่เล็กกว่ามากจนเห็นได้ชัด ขนาดโตเต็มที่ประมาณ 6 เซนติเมตร พบกระจายอยู่ทั่วไปในแหล่งน้ำนิ่งที่มีขนาดตื้นพื้นที่เล็กทั้งในภาคกลางและภาคเหนือในประเทศไทยเท่านั้น สถานะปัจจุบันในธรรมชาติถูกคุกคามจากสภาพแวดล้อมที่เปลี่ยนไปและสารเคมีที่ตกค้าง มีพฤติกรรมชอบอยู่ตัวเดียวในอาณาบริเวณแคบ ๆ เพราะดุร้ายก้าวร้าวมากในปลาชนิดเดียวกัน ตัวผู้เมื่อพบกันจะพองตัว พองเหงือก เบ่งสีเข้ากัดกัน ซึ่งในบางครั้งอาจกัดได้จนถึงตาย เมื่อผสมพันธุ์ ตัวผู้จะเป็นฝ่ายก่อหวอดติดกับวัสดุต่าง ๆ เหนือผิวน้ำ ไข่ใช้เวลาฟัก 2 วัน โดยที่ปลาตัวผู้จะเป็นฝ่ายดูแลไข่และตัวอ่อนเอง โดยไม่ให้ปลาตัวเมียเข้ามาเกี่ยวข้องด้วย</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลาเสือตอ\" src=\"http://aqua.c1ub.net/forum/index.php?action=dlattach;topic=127893.0;attach=68357\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 2&nbsp;ปลาเสือตอ</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลาเสือตอลายใหญ่ (อังกฤษ: Siamese tigerfish) เป็นปลาน้ำจืดชนิดหนึ่ง มีชื่อวิทยาศาสตร์ว่า Datnioides pulcher เป็นปลาที่อยู่ในวงศ์ปลาเสือตอ (Datnioididae) มีรูปร่างแบนข้าง ปากยาวสามารถยืดได้ ครีบก้นเล็กมีก้านครีบแข็ง 3 ชิ้น ครีบหลังแบ่งเป็น 2 ตอน ตอนแรกเป็นก้านครีบแข็งมีเงี่ยง 13 ชิ้น ตอนหลังเป็นครีบอ่อน พื้นลำตัวสีเหลืองน้ำตาลจนถึงสีส้มอมดำ มีแถบสีดำคาดขวางลำตัวในแนวเฉียงรวมทั้งสิ้นประมาณ 5-6 แถบ หรือ 7 แถบ ส่วนหัวมีลักษณะลาดเอียงมาก เกล็ดเป็นแบบสาก (Ctenoid) มีลักษณะนิสัยอยู่เป็นฝูงเล็ก ๆ ใต้น้ำ โดยมักจะอาศัยบริเวณใกล้ตอไม้หรือโพรงหินด้วยการอยู่ลอยตัวอยู่นิ่งๆ หัวทิ่มลงเล็กน้อย หากินในเวลากลางคืน โดยกินอาหารแบบฉกงับ อาหารได้แก่ สัตว์น้ำขนาดเล็กและแมลงต่างๆ มีขนาดลำตัวโตสุดประมาณ 40 เซนติเมตร หนักถึง 7 กิโลกรัม อาศัยอยู่ตามแม่น้ำสายใหญ่ในภาคกลางของประเทศไทย เช่น แม่น้ำเจ้าพระยา, แม่น้ำแม่กลอง, แม่น้ำท่าจีน ในภาคอีสานเช่น แม่น้ำโขงและสาขา ต่างประเทศพบที่กัมพูชาและเวียดนาม โดยเฉพาะทื่บึงบอระเพ็ดเป็นที่ขึ้นชื่อมากเพราะมีรสชาติอร่อย กล่าวกันว่า ใครไปถึงบึงบอระเพ็ดแล้ว ไม่ได้กินเสือตอ ถือว่าไปไม่ถึง แต่ปัจจุบัน ไม่มีรายงานการพบมานานแล้ว จนเชื่อว่าสูญพันธุ์จากธรรมชาติแล้วในประเทศไทย</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align: center;\"><img alt=\"ผลการค้นหารูปภาพสำหรับ ปลาตะพัด\" src=\"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXukAZ64C7ucoVu-4NnyuT3cBvi0XYWd2LodfTTlsCjDYrGfNo6Q\" style=\"border-style:solid; border-width:2px; height:250px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"background-color:#FFF0F5\">อันดับที่ 1&nbsp;ปลาตะพัด หรือ ปลามังกร</span></strong></span><br />\r\n<span style=\"font-size:18px\"><span style=\"background-color:#E6E6FA\">ปลาตะพัด หรือที่นิยมเรียกว่า อะโรวาน่า เป็นปลาน้ำจืดชนิดหนึ่ง ที่มีการสืบสายพันธุ์มาจากปลาในยุคก่อนประวัติศาสตร์ มีชื่อภาษาอังกฤษว่า Arowana (อะโรวาน่า) หรือ Arawana (อะราวาน่า) มีชื่อวิทยาศาสตร์ว่า Scleropages formosus อยู่ในวงศ์ปลาตะพัด (Osteoglossidae) นับว่าเป็นปลาที่ใกล้สูญพันธุ์ในธรรมชาติ เนื่องจากเป็นปลาที่สืบพันธุ์ยาก ประกอบกับแหล่งที่อยู่ถูกทำลายไปได้รับความนิยมอย่างสูงของนักเลี้ยงปลาตู้ ในฐานะของปลาสวยงาม ราคาแพง สำหรับชื่อ &quot;ตะพัด&quot; เป็นชื่อที่เรียกกันในภาคตะวันออก แถบจังหวัดจันทบุรีและตราด ในภาคใต้จังหวัดสุราษฎร์ธานีจะเรียกปลาชนิดนี้ว่า &quot;หางเข้&quot; ถูกค้นพบเป็นทางการครั้งแรกในประเทศไทยเมื่อปี พ.ศ. 2474 ตามรายงานของสมิธที่ลำน้ำเขาสมิง จังหวัดตราด โดยระบุว่าในขณะนั้น ปลาตะพัดเป็นปลาที่พบได้ทั่วไปในแม่น้ำลำคลองในภาคตะวันออก ไข่มีลักษณะสีส้มลูกกลมใหญ่ ฟักไข่ในปาก เนื้อมีรสชาติอร่อย นิยมใช้ทำเป็นอาหาร ในปัจจุบัน สำหรับประเทศไทย เชื่อว่าเหลือเพียงบริเวณเขตรักษาพันธุ์สัตว์ป่าคลองแสงและเขตรักษาพันธุ์สัตว์ป่าคลองยัน ซึ่งเป็นต้นแม่น้ำตาปี และบริเวณแม่น้ำ ที่อำเภอละงู จังหวัดสตูลเท่านั้น ส่วนทางภาคตะวันออกที่เคยชุกชุมในอดีต ไม่มีรายงานการพบอีกเลย อีกที่หนึ่งที่ได้เคยได้ชื่อว่ามีปลาตะพัดชุกชุมคือ บึงน้ำใส อำเภอรามัน จังหวัดยะลา ในอดีตเป็นแหล่งจับปลาตะพัดที่มีชื่อเสียงมาก จนมีชื่อปรากฏในคำขวัญประจำอำเภอ โดยชาว</span><a href=\"http://home.sanook.com/\" target=\"_blank\" title=\"บ้าน\"><span style=\"background-color:#E6E6FA\">บ้าน</span></a><span style=\"background-color:#E6E6FA\">จะเรียกปลาชนิดนี้ว่า &quot;กรือซอ&quot; แต่จากการจับอย่างมากในอดีต ทำให้ในปัจจุบัน ปริมาณปลาตะพัดลดน้อยลงจนแทบจะสูญพันธุ์</span></span></p>\r\n\r\n<hr />\r\n<p><br />\r\n&nbsp;</p>\r\n', 33, '29/05/2017 07:42:37', '');

